class KhqrStandWidget extends StatelessWidget {
  const KhqrStandWidget({
    super.key,
    this.color,
    required this.qrData,
    this.qrIcon,
    this.logo,
    required this.name,
    this.bakongId,
    this.width = 300,
  });

